import React from "react";
import { connect } from "react-redux";
import { getProjectList } from "../../actions";

class ProjectList extends React.Component {
  componentDidMount() {
    this.props.getProjectList();
  }

  renderList() {
    return this.props.projects.map((stream) => {
      return (
        <div className="item" key={stream.id}>
          <i className="large middle aligned icon camera" />
          <div className="content">
            {stream.title}
            <div className="description">{stream.description}</div>
          </div>
        </div>
      );
    });
  }

  render() {
    return <div>Showing Project List for {this.props.name}</div>;
  }
}
const mapPropToState = (state) => {
  return { projects: Object.values(state.projects) };
};

export default connect(mapPropToState, { getProjectList })(ProjectList);
