import React from "react";
import { Field, reduxForm } from "redux-form";
import Select from "react-select";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import _ from "lodash";



class ProjectForm extends React.Component {


  renderOptions=(options)=>{

    
    var keyMap = {
      id: 'value',
      companyName: 'label',
      userName:'label',
      description:'label'
    };
    
    const modifiedOptions=  options.map(function(obj) {
      return _.mapKeys(obj, function(value, key) {
        return keyMap[key];
      });
    });

    return modifiedOptions;
  }


  renderTextInput = ({ input, label, type = "text" }) => {
   
    return (
      <div className="field">
        {this.renderLabel(label)}

        <div className="ui labeled input">
          <input {...input} type={type} />
        </div>
      </div>
    );
  };

  renderDropdown = ({ input, options, label,isMulti="" }) => {
    
    

    return (
      <div className="field">
        {this.renderLabel(label)}

        <Select 
          className="basic-multi-select"
          classNamePrefix="select"
          {...input}
          options={options}
          {...isMulti}
        />
      </div>
    );
  };

  renderLabel = (label) => {
    return <label>{label}:</label>;
  };

  renderDatePicker = ({ input, label, minDate = new Date() }) => {
    return (
      <div className="field">
        {this.renderLabel(label)}
        <DatePicker dateFormat="dd/MM/yyyy" {...input} minDate={minDate}
        selected={input.value} />
      </div>
    );
  };

  render() {
    console.log(" PROPS IN FORM ",this.props);
   
    if(this.props.client){
      console.log("AS OBJECS",this.renderOptions(this.props.initialValues.client));

      
    const priorty= [
      { value: "LOW", label: "LOW" },
      { value: "MEDIUM", label: "MEDIUM" },
      { value: "HIGH", label: "HIGH" },
    ];

      return (

      
        <form className="ui form">
          <div className="three fields">
            <Field
              name="qouteNumber"
              label="Qoute Number"
              type="number"
              component={this.renderTextInput}
            />
            <Field
              name="projectName"
              label="Project Name"
              component={this.renderTextInput}
            />
  
            <Field
              name="selectedClient"
              component={this.renderDropdown}
              options={this.renderOptions(this.props.initialValues.client)}
              label="Client"
            />
          </div>
  
          <div className="three fields">
            <Field
              name="selectedPriorty"
              component={this.renderDropdown}
              options={priorty}
              label="Priority"
            />
            <Field
              name="startDate"
              component={this.renderDatePicker}
              label="Start Date"
            />
  
            <Field
              name="selectedType"
              component={this.renderDropdown}
              options={this.renderOptions(this.props.initialValues.type)}
              label="Project Type"
            />
          </div>
  
          <div className="three fields">
            <Field
              name="selectedEmployee" 
              component={this.renderDropdown}
              options={this.renderOptions(this.props.initialValues.employee)}
              label="Allocation"
            />
            <Field
              name="estEndDate"
              component={this.renderDatePicker}
              label="Est End Date"
            />
  
            <Field
              name="selectedGuide"
              component={this.renderDropdown}
              options={this.renderOptions(this.props.initialValues.employee)}
              label="Guide"
            />
          </div>
        </form>
      );
    }

    return <div>Loading</div>;
    
  
  }
}



export default reduxForm({
  form: "projectForm",
})(ProjectForm);
