import React from "react";
import { Field, reduxForm } from "redux-form";
import Select from "react-select";

class ProjectForm extends React.Component {
  renderTextInput = ({ input, label, type = "text" }) => {
    console.log(input);
    return (
      <div className="field">
        <label>{label}:</label>

        <div className="ui labeled input">
          <input {...input} type={type} />
        </div>
      </div>
    );
  };

  renderDropdown = ({ input, options, label }) => {
    console.log(options);
    return (
      <div className="field">
        <label>{label}:</label>

        <Select
          className="basic-multi-select"
          classNamePrefix="select"
          {...input}
          options={options}
        />
      </div>
    );
  };

  render() {
    const userOptions = [
      {
        label: "Erika",
        value: "4e4cf51f-b406-413a-ae46-2cf06c7aabff",
      },
      {
        label: "Julia",
        value: "edad97c7-f2dc-4198-91a9-8f20c7bc67b2",
      },
      {
        label: "Sarah",
        value: "57d3578a-3583-4290-8bae-596a4da81a8d",
      },
    ];

    return (
      <form className="ui form">
        <div className="three fields">
          <Field
            name="qouteNumber"
            label="Qoute Number"
            type="number"
            component={this.renderTextInput}
          />
          <Field
            name="projectName"
            label="Project Name"
            component={this.renderTextInput}
          />

          <Field
            name="selectedClient"
            component={this.renderDropdown}
            options={userOptions}
            label="Client"
          />

          
        </div>

        <div className="three fields">
          <Field
            name="qouteNumber"
            label="Qoute Number"
            type="number"
            component={this.renderTextInput}
          />
          <Field
            name="projectName"
            label="Project Name"
            component={this.renderTextInput}
          />

          <Field
            name="selectedClient"
            component={this.renderDropdown}
            options={userOptions}
            label="Client"
          />

          
        </div>
      </form>
    );
  }
}

export default reduxForm({
  form: "projectForm",
})(ProjectForm);
