import React from "react";

class GoogleAuth extends React.Component {
  state = { isSignedIn: null, userName: null };

  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "797401886567-9cumct9mrt3v2va409rasa7fa6fq02hh.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSignedIn: this.auth.isSignedIn.get() });

          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }

  onAuthChange = () => {
    this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    if (this.state.isSignedIn) {
      console.log("yes");
      this.setState({
        userName: this.auth.currentUser.get().getBasicProfile().getName(),
      });
    }
  };

  onSignIn = () => {
    this.auth.signIn();
  };

  onSignOut = () => {
    this.auth.signOut();
  };

  renderAuthButton() {
    if (this.state.isSignedIn === null) {
      return null;
    } else if (this.state.isSignedIn) {
      console.log(this.state.userName);
      return (
        <React.Fragment>
          <div className="item">Welcome {this.state.userName}</div>
          <div className="item">
          <div onClick={this.onSignOut} className="ui red google button">
            <i className="google icon" />
            Sign Out
          </div>
          </div>
          
        </React.Fragment>
      );
    } else {
      return (
        <button onClick={this.onSignIn} className="ui red google button">
          <i className="google icon" />
          Sign In
        </button>
      );
    }
  }

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

export default GoogleAuth;
