import React from "react";
import { Field, reduxForm } from "redux-form";
import Select from "react-select";

class ProjectForm extends React.Component {

    

  renderTextInput = ({input,label,type="text"}) => {

    
   console.log(input);
    return (
      <div className="field">
        <label>{label}:</label>

        <div className="ui labeled input">
         
          <input
            {...input}
           type={type}

          />
        </div>
      </div>
    );
  };


  renderDropdown=({input,label})=>{
    
    return (
        <div className="field">
          <label>{label}:</label>
  
          <div className="ui labeled input">
           
            <select
              {...input}
            
  
            />
          </div>
        </div>
      );
  };

  render() {


    let colors = ['orange', 'red', 'blue', 'purple'];

    return (
       

      <form className="ui form">

        <div className="three fields">  
        <Field
          
          name="qouteNumber"
          label="Qoute Number"
          type="number"
          component={this.renderTextInput}
        />
        <Field
          
          name="projectName"
          label="Project Name"
          component={this.renderTextInput}
        />

        <Field
            name="selectedClient"
            label="Client"
            options={colors}
            component={this.renderDropdown}
            
        />
        </div>
      </form>
    );
  }
}

export default reduxForm({
  form: "projectForm",
})(ProjectForm);
