import React from 'react';

class GoogleAuth extends React.Component {

    state = { isSignedIn: null }

    componentDidMount() {

        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '355659458945-3gv1ab3kff92o3sgtnjemk45rhe7vm90.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                const isSignedIn=(this.auth.isSignedIn.get());
                console.log(isSignedIn, " SINGED IN STATUS");
                this.setState({ isSignedIn: isSignedIn });
            });
        });
    }


    getAuthStatus = () => {
        this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    }

    onSignIn = () => {
        this.auth.signIn();
    }

    onSignOut = () => {
        this.auth.signOut();
    }


    render() {



        if (this.state.isSignedIn === null) {
            return (
                <div className="item">
                    <div className="ui button">Loading</div>
                </div>
            );
        } else if (this.state.isSignedIn) {
            return (
                <div className="item">
                    <button className="ui red google button" onClick={this.onSignOut}>
                        <i className="google icon"></i>
                        Sign out</button>
                </div>
            );
        } else if (!this.state.isSignedIn) {
            (
                <div className="item">
                    <button className="ui  red google button" onClick={this.onSignIn}>
                        <i className="google icon"></i>Sign in</button>
                </div>
            )
        }else{
            return null;
        }

    };
}


export default GoogleAuth;
