import React from 'react';

class GoogleAuth extends React.Component {

    state = { isSignedIn: null }

    componentDidMount() {

        window.gapi.load('client:auth2', () => {
            window.gapi.init({
                clientId: '355659458945-3gv1ab3kff92o3sgtnjemk45rhe7vm90.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn: this.auth.isSignedIn.get() });
            });
        });
    }

    render() {



        if (this.state.isSignedIn === null) {
            return (
                <div className="item">
                    <div className="ui button">Loading</div>
                </div>
            );
        } else if (this.state.isSignedIn) {
            return (
                <div className="item">
                    <div className="ui primary button">Log out</div>
                </div>
            );
        } else if (!this.state.isSignedIn) {
            (
                <div className="item">
                    <div className="ui primary button">Log in</div>
                </div>
            )
        }

    };
}


export default GoogleAuth;
