import React from "react";
import { Field, reduxForm } from "redux-form";
import Select from "react-select";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import _ from "lodash";



class ProjectForm extends React.Component {




  renderTextInput = ({ input, label, type = "text" }) => {
   
    return (
      <div className="field">
        {this.renderLabel(label)}

        <div className="ui labeled input">
          <input {...input} type={type} />
        </div>
      </div>
    );
  };

  renderDropdown = ({ input, options, label }) => {
    
    

    return (
      <div className="field">
        {this.renderLabel(label)}

        <Select
          className="basic-multi-select"
          classNamePrefix="select"
          {...input}
          options={displayOptions}
        />
      </div>
    );
  };

  renderLabel = (label) => {
    return <label>{label}:</label>;
  };

  renderDatePicker = ({ input, label, minDate = new Date() }) => {
    return (
      <div className="field">
        {this.renderLabel(label)}
        <DatePicker dateFormat="dd/MM/yyyy" {...input} minDate={minDate}
        selected={input.value} />
      </div>
    );
  };

  render() {
    console.log(" PROPS IN FORM ",this.props);
   
    const displayOptions=_.mapKeys(this.props.initialValues.client, 'id');
    

    const userOptions = [
      {
        label: "Erika",
        value: "4e4cf51f-b406-413a-ae46-2cf06c7aabff",
      },
      {
        label: "Julia",
        value: "edad97c7-f2dc-4198-91a9-8f20c7bc67b2",
      },
      {
        label: "Sarah",
        value: "57d3578a-3583-4290-8bae-596a4da81a8d",
      },
    ];

    return (

      
      <form className="ui form">
        <div className="three fields">
          <Field
            name="qouteNumber"
            label="Qoute Number"
            type="number"
            component={this.renderTextInput}
          />
          <Field
            name="projectName"
            label="Project Name"
            component={this.renderTextInput}
          />

          <Field
            name="selectedClient"
            component={this.renderDropdown}
            options={this.props.initialValues.client}
            label="Client"
          />
        </div>

        <div className="three fields">
          <Field
            name="selectedPriorty"
            component={this.renderDropdown}
            options={userOptions}
            label="Priority"
          />
          <Field
            name="startDate"
            component={this.renderDatePicker}
            label="Start Date"
          />

          <Field
            name="selectedType"
            component={this.renderDropdown}
            options={userOptions}
            label="Project Type"
          />
        </div>

        <div className="three fields">
          <Field
            name="selectedEmployee"
            component={this.renderDropdown}
            options={userOptions}
            label="Allocation"
          />
          <Field
            name="estEndDate"
            component={this.renderDatePicker}
            label="Est End Date"
          />

          <Field
            name="selectedGuide"
            component={this.renderDropdown}
            options={userOptions}
            label="Guide"
          />
        </div>
      </form>
    );
  }
}



export default reduxForm({
  form: "projectForm",
})(ProjectForm);
